-- Create the database

CREATE DATABASE db;

USE db;

CREATE TABLE car_list (
  Id INTEGER NOT NULL AUTO_INCREMENT,
  Car_Make VARCHAR(13) NOT NULL,
  Car_Model VARCHAR(29) NOT NULL,
  Production_Year INTEGER NOT NULL,
  Engine_Size_L VARCHAR(20) NOT NULL,
  Horsepower VARCHAR(7) NOT NULL,
  Torque_lbft VARCHAR(7) NOT NULL,
  060_MPH_Time_seconds VARCHAR(5) NOT NULL,
  Price_in_USD INTEGER NOT NULL,
  PRIMARY KEY (Id)
);

GRANT privileges ON db.car_list TO 'user'@'hostname';

